import React, { useState, useEffect } from 'react';
import { User, Settings, Shield, Moon, Sun, Camera, ChevronLeft, BrainCircuit } from 'lucide-react';
import StatCard from './components/StatCard';
import SettingsItem from './components/SettingsItem';
import { UserStats, DecisionAnalysis } from './types';
import { analyzeDecisionStyle } from '../../../services/deepseekService';
import { authService, dbService } from '../../../services/supabaseService';
import { useTheme } from '../../../contexts/ThemeContext';

const LightProfile: React.FC = () => {
  const [view, setView] = useState<'profile' | 'decision' | 'loading'>('profile');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState<DecisionAnalysis | null>(null);
  const [stats, setStats] = useState<UserStats>({
    associateId: "#0812",
    clarityLevel: 9,
    totalDuels: "1,402",
    winRate: "68%",
    lastDecision: "2h ago",
    logicScore: 942,
    progress: 9.4
  });

  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    // Supabase 版本暂不做 profile 表同步，这里保留默认 stats，不影响 UI
    void authService.getCurrentUser();
  }, []);

  const handleAccountClick = async () => {
    const user = await authService.getCurrentUser();
    if ((user as any)?.email) {
      alert(`Current account: ${(user as any).email}`);
    } else {
      window.location.hash = '#/auth/login';
    }
  };

  const handleHistoryPrivacyClick = () => {
    // Jump to the corresponding history screen so user can review/clean their own traces
    window.location.hash = theme === 'dark' ? '#/history-dark' : '#/history';
  };

  const handleDecisionAnalysis = async () => {
    setIsAnalyzing(true);
    setView('loading');
    try {
      const user = await authService.getCurrentUser();
      let recentLog = '';

      if (user) {
        // Pull recent real decisions from Firestore/local, and synthesize a compact log string
        const decisions: any[] = await dbService.getDecisions((user as any).id, 20);
        if (decisions && decisions.length > 0) {
          recentLog = decisions
            .map(d => {
              const title = d.title || d.category || '';
              const winner = d.winner || '';
              const losers = Array.isArray(d.eliminated) ? d.eliminated.join(', ') : '';
              return `In "${title}", chose "${winner}" over [${losers}].`;
            })
            .join(' ');
        }
      }

      if (!recentLog) {
        recentLog = "User has made only a few decisions so far; infer style from limited samples.";
      }

      const result = await analyzeDecisionStyle(recentLog);
      setAnalysisResult(result);
      setView('decision');
    } catch (error) {
      console.error("Analysis failed", error);
      setView('profile');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleSignOut = async () => {
    try {
      await authService.signOut();
      window.location.hash = '#/auth/login';
    } catch (e) {
      console.error(e);
    }
  };

  if (view === 'loading') {
    return (
      <div className="min-h-screen bg-[#f9f9f9] flex flex-col items-center justify-center p-8 text-center">
        <div className="w-16 h-16 border-4 border-gray-200 border-t-black rounded-full animate-spin mb-6"></div>
        <h2 className="text-2xl font-bold mb-2">Analyzing Logic Streams</h2>
        <p className="text-gray-500 max-w-xs">Connecting to the associate matrix to parse neural decision weights...</p>
      </div>
    );
  }

  if (view === 'decision') {
    return (
      <div className="min-h-screen bg-[#f9f9f9] max-w-md mx-auto p-8 pt-12">
        <button onClick={() => setView('profile')} className="mb-8 flex items-center gap-2 text-gray-500 font-medium">
          <ChevronLeft className="w-5 h-5" /> Back to Profile
        </button>
        
        <div className="bg-white rounded-[40px] p-8 shadow-sm border border-gray-100">
          <div className="w-16 h-16 bg-black rounded-2xl flex items-center justify-center mb-6 text-white">
            <BrainCircuit className="w-8 h-8" />
          </div>
          <h1 className="text-3xl font-black mb-1">Decision Style</h1>
          <p className="text-gray-400 font-medium mb-8">Generated by Neural Engine</p>
          
          <div className="space-y-6">
            <div>
              <h3 className="text-xs font-black uppercase tracking-widest text-gray-400 mb-2">Dominant Archetype</h3>
              <p className="text-xl font-bold text-black">{analysisResult?.style || 'Synthesizer'}</p>
            </div>
            
            <div>
              <h3 className="text-xs font-black uppercase tracking-widest text-gray-400 mb-2">Summary</h3>
              <p className="text-gray-600 leading-relaxed">{analysisResult?.summary}</p>
            </div>

            <div>
              <h3 className="text-xs font-black uppercase tracking-widest text-gray-400 mb-2">Identified Fallacies</h3>
              <div className="flex flex-wrap gap-2 mt-2">
                {analysisResult?.logicalFallacies.map((f, i) => (
                  <span key={i} className="px-3 py-1 bg-gray-100 rounded-full text-[12px] font-bold text-gray-600 uppercase tracking-tight">
                    {f}
                  </span>
                ))}
                {analysisResult?.logicalFallacies.length === 0 && <p className="text-green-600 font-bold">None detected.</p>}
              </div>
            </div>

            <div className="pt-6 border-t border-gray-100">
              <h3 className="text-xs font-black uppercase tracking-widest text-gray-400 mb-2">Next Directive</h3>
              <p className="text-black italic font-medium">"{analysisResult?.recommendation}"</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#f9f9f9] max-w-md mx-auto p-8 pt-12">
      {/* Header / Profile */}
      <div className="flex flex-col items-center mb-12">
        <div className="relative mb-6">
          <div className="w-40 h-40 rounded-full border border-gray-300 p-1 flex items-center justify-center">
             {/* Progress Circle Visual */}
            <svg className="absolute inset-0 w-full h-full -rotate-90">
                <circle 
                  cx="80" cy="80" r="78" 
                  fill="none" 
                  stroke="#e5e7eb" 
                  strokeWidth="2"
                />
                <circle 
                  cx="80" cy="80" r="78" 
                  fill="none" 
                  stroke="black" 
                  strokeWidth="2" 
                  strokeDasharray="490" 
                  strokeDashoffset={490 - (490 * (stats.progress / 10))}
                />
            </svg>
            <div className="w-32 h-32 rounded-full bg-gray-200 flex items-center justify-center overflow-hidden">
               <User className="w-16 h-16 text-gray-400" />
            </div>
          </div>
          {/* Edit Badge */}
          <div className="absolute top-0 right-2 w-8 h-8 bg-black rounded-full flex items-center justify-center text-white border-2 border-[#f9f9f9] cursor-pointer shadow-lg active:scale-90 transition-transform">
            <Camera className="w-4 h-4" />
          </div>
          {/* Progress Badge */}
          <div className="absolute bottom-4 right-2 w-10 h-10 bg-white rounded-full flex items-center justify-center text-[13px] font-black border-2 border-[#f9f9f9] shadow-lg">
            {stats.progress}
          </div>
        </div>
        
        <h1 className="text-3xl font-black mb-2">Associate {stats.associateId}</h1>
        <div className="flex items-center gap-2">
            <div className="w-1.5 h-1.5 rounded-full bg-black"></div>
            <span className="text-[11px] font-black text-gray-400 uppercase tracking-[0.25em]">Clarity Level {stats.clarityLevel}</span>
        </div>
      </div>

      {/* Grid Stats */}
      <div className="grid grid-cols-2 gap-4 mb-12">
        <StatCard value={stats.totalDuels} label="Total Duels" />
        <StatCard value={stats.winRate} label="Win Rate" />
        <StatCard value={stats.lastDecision} label="Last Decision" />
        <StatCard value={stats.logicScore} label="Logic Score" />
      </div>

      {/* Preferences Section */}
      <div className="mb-12">
        <h2 className="text-[11px] font-black text-gray-400 uppercase tracking-widest mb-4 ml-1">System Preferences</h2>
        <div className="space-y-1">
          <SettingsItem 
            icon={<User className="w-5 h-5" />} 
            label="Account" 
            onClick={handleAccountClick}
          />
          <SettingsItem 
            icon={<BrainCircuit className="w-5 h-5" />} 
            label="Decision Style" 
            onClick={handleDecisionAnalysis}
          />
          <SettingsItem 
            icon={<Shield className="w-5 h-5" />} 
            label="History Privacy" 
            onClick={handleHistoryPrivacyClick}
          />
          <SettingsItem 
            icon={theme === 'light' ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />} 
            label="App Theme" 
            value={theme === 'light' ? 'Light' : 'Dark'}
            onClick={toggleTheme}
          />
        </div>
      </div>

      {/* Footer */}
      <div className="flex flex-col items-center gap-8 pb-12">
        <button
          onClick={handleSignOut}
          className="text-sm font-black uppercase tracking-widest border-b-2 border-black pb-1 hover:opacity-70 transition-opacity"
        >
          Sign Out
        </button>
        
        <div className="w-10 h-10 border-2 border-gray-300 rounded flex items-center justify-center text-xs font-black text-gray-400">
          D
        </div>
      </div>
    </div>
  );
};

export default LightProfile;

